import React, { Component } from "react";
import "./App.css";
import AboutUs from "./AboutUs";
import ContactUS from "./ContactUS";
import logo from "./logo-black.png";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { menu: "1" };

    this.onClick = this.state.onClick.bind(this);
  }

  onClick() {
    this.setState({
      menu: "0"
    });
  }
  render() {
    const showAboutUs = this.state.menu === "0";
    const showContactUs = this.state.menu === "1";
    return (
      <div className="wrapper" style={{ display: "block" }}>
        <div className="siteLogo" style={{ padding: "50px 0  0 50px" }}>
          <a href="https://apologia.jp/index.html">
            <img src={logo} width={300} />
          </a>
        </div>
        <div className="app-body">
          <div className="app-side-bar">
            <a onClick={this.onClick} className="app-navigation">
              About us
            </a>

            <a
              href="https://apologia.jp/collection/"
              className="app-navigation"
            >
              Gallery
            </a>

            <a
              href="https://apologia.jp/about/index.html"
              className="app-navigation"
            >
              Contact us
            </a>
          </div>
          <div className="contents">
            {showAboutUs && <AboutUs />}
            {showContactUs && <ContactUS />}
          </div>
        </div>
      </div>

      // <Pages></Pages>
    );
  }
}

export default App;
