import React, { Component } from "react";
import "./App.css";
import AboutUs from "./AboutUs";
import ContactUS from "./ContactUS";
import Gallery from "./Gallery";
import logo from "./logo-black.png";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = { menu: "1" };
    this.handleAbout = this.handleAbout.bind(this);
    this.handleContact = this.handleContact.bind(this);
    this.handleGallery = this.handleGallery.bind(this);
  }

  handleAbout() {
    this.setState(state => ({
      menu: "0"
    }));
  }

  handleGallery() {
    this.setState(state => ({
      menu: "1"
    }));
  }

  handleContact() {
    this.setState(state => ({
      menu: "2"
    }));
  }

  render() {
    const showAboutUs = this.state.menu === "0";
    const showGallery = this.state.menu === "1";
    const showContactUs = this.state.menu === "2";
    return (
      <div className="wrapper" style={{ display: "block" }}>
        <div className="siteLogo" style={{ padding: "50px 0  0 50px" }}>
          <a href="#">
            <img src={logo} width={300} />
          </a>
        </div>
        <div className="app-body">
          <div className="app-side-bar">
            <a onClick={this.handleAbout} className="app-navigation">
              About us
            </a>

            <a
              href="https://apologia.jp/collection/"
              className="app-navigation"
            >
              Gallery
            </a>

            <a
              onClick={this.handleContact}
              className="app-navigation"
              className="app-navigation"
            >
              {" "}
              Contact us
            </a>
          </div>
          <div className="contents">
            {showAboutUs && <AboutUs />}
            {showGallery && <Gallery />}
            {showContactUs && <ContactUS />}
          </div>
        </div>
      </div>
    );
  }
}

export default App;
